# ===========start define sonicpi program===========
# ==================================================

# plays the sample of the given instrument at the relevant pitch for the note desired
# the note duration is used to set up the envelope parameters
define :pl do | instrument, samplepitch, noteval, durval, amp = 1, pan = 0 |
  samplerate = pitch_to_ratio ( note(noteval) - note(samplepitch) )
  sample orchestra, instrument, rate: samplerate, attack: 0.02, attack_level: 1.2, decay: 0.1, sustain_level: 1, sustain: 0.9 * durval, release: 0.1* durval, amp: amp, pan: pan
end

# note duration scale factor: 
# redefined in use_bpm function
samplingrate = 1 

# sets bpm required adjusting note duration variables accordingly
# d is divisions (pulses) in a beat (crochet)
define :set_bpm do |d,n|
  # 60 seconds to play n beats per min i.e n crochets per min i.e n * 8 pulses per minute
  samplingrate = 60 / (d * n.to_f) # time in seconds to complete a pulse
end

# plays an array of notes and associated array of durations
# parameters are instrument name, sample pitch, notes array, notes durations array, shift (transpose), amp and pan
define :plarray do | part, directions, instrument, samplepitch, notesmeasures, durationsmeasures, shift = -12, amp = 1, pan = 0 |
  measuresindices = (1..(notesmeasures.length)).to_a
  measuresindices.zip(notesmeasures, durationsmeasures) do | measureindex, notesarray, durationsarray| 
    notesindices = (1..(notesarray.length)).to_a
    currentrate = samplingrate
    notesindices.zip(notesarray, durationsarray) do | noteindex, noteval, durval |
      puts( ' >>> part: ' + part.to_s + ' measure: ' + measureindex.to_s + ' note: ' + noteindex.to_s + ' samplingrate: ' + currentrate.to_s ) 
      if (part == 'P1')
        directn = directions.find_all{|d| d[0] == measureindex && d[1] == noteindex - 1 }        
        if(!directn.empty?)
          # puts('samplingrate BEFORE====', part, currentrate, samplingrate)
          set_bpm(480, directn[0][2])
          # puts(part, 'set bpm to', directn[0][2], (' measure: ' + measureindex.to_s + ' note: ' + noteindex.to_s ) )
          # puts('samplingrate AFTER======', part, currentrate, samplingrate)
          # puts(directn, directions.length, part)
        else
          # puts('maintain====', part, currentrate, samplingrate)
        end
      else
        # puts('inherit====', part, currentrate, samplingrate)
      end
      # puts('noteval======', part, measureindex, noteindex, noteval, durval)
      if noteval != :r
        if noteval.respond_to?(:each)
          noteval.each do |nv|
            pl(instrument, samplepitch, note(nv) + shift, durval * currentrate, amp, pan)
          end
        else
          pl(instrument, samplepitch, note(noteval) + shift, durval * currentrate, amp, pan)
        end
      end
      sleep durval * currentrate
    end
  end
end

# ==================================================
# ===========end define sonicpi program=============


